// Process and filter AI news articles
const items = $input.all();
const processedArticles = [];

for (const item of items) {
  const data = item.json;

  // Filter for AI-related content
  if (data.title && (
    data.title.toLowerCase().includes('ai') ||
    data.title.toLowerCase().includes('artificial intelligence') ||
    data.title.toLowerCase().includes('machine learning') ||
    data.title.toLowerCase().includes('openai') ||
    data.title.toLowerCase().includes('chatgpt') ||
    data.title.toLowerCase().includes('gpt') ||
    data.title.toLowerCase().includes('claude') ||
    data.title.toLowerCase().includes('gemini')
  )) {
    processedArticles.push({
      title: data.title,
      excerpt: data.description || data.summary || '',
      content: data.content || data.description || '',
      source: data.link ? new URL(data.link).hostname : 'Unknown',
      url: data.link,
      timestamp: new Date().toISOString(),
      category: 'AI News',
      readTime: Math.ceil((data.content || data.description || '').split(' ').length / 200) + ' min read',
      trending: Math.random() > 0.7, // Random trending for demo
      image: data.enclosure?.url || 'https://images.unsplash.com/photo-1677442136019-21780ecad995?w=400&h=300&fit=crop',
      tags: ['AI', 'Artificial Intelligence', 'Technology']
    });
  }
}

return processedArticles.map(article => ({ json: article }));
